
var rapidlib = require('./Rapidlib.js');

var myClassification = new rapidlib.Classification();

let trainingSet = [];
let rapidInput = [2, 4];
let rapidOutput = [0]
trainingSet.push({
    input:rapidInput,
    output:rapidOutput
});

rapidInput = [1, 5];
rapidOutput = [1]

trainingSet.push({
    input:rapidInput,
    output:rapidOutput
});
myClassification.train(trainingSet);

console.log(myClassification.process([1,5]));
console.log(myClassification.process([2,4]));


console.log(myClassification.process([2.1,4.1]));